		WHERE bin.del_yn='N'
				AND bin.uid_company 		= 		#{uid_company}
				AND bin.uid_rack 			= 		#{uid_rack}
				AND UPPER(bin.bin_code) 	like 	UPPER(#{bin_code})
				AND UPPER(bin.bin_name) 	like 	UPPER(#{bin_name})
				    AND bin.bin_row 			= 		#{bin_row}
				    AND bin.bin_col 			= 		#{bin_col}
					AND bin.remark 			= 		#{remark}

				AND	bin.creator_uid 	= 		#{creator_uid}
					AND	bin.creator 		like 	#{creator}
				AND DATE_FORMAT(bin.create_date, '%Y-%m-%d') BETWEEN #{create_date_s} AND #{create_date_e}
				AND	bin.changer_uid 	= 		#{changer_uid}
					AND	bin.changer 		like 	#{changer}
				AND DATE_FORMAT(bin.change_date, '%Y-%m-%d') BETWEEN #{change_date_s} AND #{change_date_e}

				AND whouse.unique_id	= 		#{uid_whouse}

					AND	bin.bin_code 		= 		#{barcode}
				AND	bin.unique_id 		= 		#{unique_id}

					AND	UPPER(whouse.wh_code) 		like 	UPPER(#{wh_code})
				AND	UPPER(rack.rack_code) 	    like 		UPPER(#{rack_code})
				    AND	location.lotno 		= 		#{lotno}

mapper.update:
/*update-bymap-BinMan*/
UPDATE TB_BIN
	
WHERE
	unique_id = #{unique_id}
	AND del_yn='N'

mapper.select:
/*getUidBinByItemCode*/SELECT
unique_id as result
FROM tb_bin
WHERE del_yn = 'N'
AND uid_company = #{uid_company}
AND bin_code = #{bin_code}

/*getUidBinManByBinCode*/SELECT
unique_id as result
FROM tb_bin
WHERE del_yn = 'N'
AND uid_company = #{uid_company}
AND bin_code = #{bin_code}

/*getRackGroup*/SELECT
rack_code
FROM TB_BIN bin
INNER JOIN TB_RACK rack on rack.unique_id = bin.uid_rack
INNER JOIN TB_WHOUSE whouse on whouse.unique_id = rack.uid_whouse
WHERE bin.del_yn='N'
AND bin.uid_company = #{uid_company}
AND whouse.unique_id = #{uid_whouse}
GROUP BY rack.unique_id
