mapper.sql:
WHERE company.del_yn='N'
			AND company.cp_code 		= 		#{cp_code}
			AND company.cp_name 		= 		#{cp_name}
			AND company.biz_no 			= 		#{biz_no}
		AND company.biz_cond 		= 		#{biz_cond}
	AND company.biz_category 	= 		#{biz_category}
	AND company.cp_president 	= 		#{cp_president}
		AND company.zip_code 		= 		#{zip_code}
		AND company.address_kr 		= 		#{address_kr}
		AND company.address_en 		= 		#{address_en}
			AND company.tel_no 			= 		#{tel_no}
			AND company.fax_no 			= 		#{fax_no}
	AND company.email_address 	= 		#{email_address}
		AND company.code_type 	= 		#{code_type}

		AND	company.creator_uid 	= 		#{creator_uid}
			AND	company.creator 		like 	#{creator}
		AND DATE_FORMAT(company.create_date, '%Y-%m-%d') BETWEEN #{create_date_s} AND #{create_date_e}
		AND	company.changer_uid 	= 		#{changer_uid}
			AND	company.changer 		like 	#{changer}
		AND DATE_FORMAT(company.change_date, '%Y-%m-%d') BETWEEN #{change_date_s} AND #{change_date_e}

		AND	company.unique_id 		= 		#{unique_id}

mapper.update:
/*update-bymap-company*/
UPDATE TB_COMPANY

WHERE 
	unique_id = #{unique_id} 
	AND del_yn='N'
